mapper.select:
SELECT A.NEWS_SEQ_NO , A.LANG_CD , A.NEWS_CCD1 , FN_GETCODENM('CELLO_NEWS', A.NEWS_CCD1, A.LANG_CD) AS NEWS_CCD_NM , A.NEWS_CCD2 , A.TITLE_NM , A.SUMMARY_INFO , A.PC_LIST_IMG_PATH , A.PC_LIST_IMG_ORG_FILE_NM , A.PC_LIST_IMG_FILE_NM , A.PC_LIST_IMG_SIZE , A.PC_LIST_IMG_ALT , A.PC_DETL_IMG_PATH , A.PC_DETL_IMG_ORG_FILE_NM , A.PC_DETL_IMG_FILE_NM , A.PC_DETL_IMG_SIZE , A.PC_DETL_IMG_ALT , A.MOBILE_LIST_IMG_PATH , A.MOBILE_LIST_IMG_ORG_FILE_NM , A.MOBILE_LIST_IMG_FILE_NM , A.MOBILE_LIST_IMG_SIZE , A.MOBILE_LIST_IMG_ALT , A.MOBILE_DETL_IMG_PATH , A.MOBILE_DETL_IMG_ORG_FILE_NM , A.MOBILE_DETL_IMG_FILE_NM , A.MOBILE_DETL_IMG_SIZE , A.MOBILE_DETL_IMG_ALT , A.NEWS_DETL_INFO , FN_GETCODENM('USE_YN', A.USE_YN, A.LANG_CD) AS USE_YN_NM , A.SRCH_CNT , A.ORDB , TO_CHAR(A.INS_DTM, 'YYYY-MM-DD') AS INS_DTM , A.INS_PERSON_ID , FN_GETMANAGERNM(A.INS_PERSON_ID) AS INS_PERSON_NM , TO_CHAR(A.UPD_DTM, 'YYYY-MM-DD') AS UPD_DTM , A.UPD_PERSON_ID , FN_GETMANAGERNM(A.UPD_PERSON_ID) AS UPD_PERSON_NM FROM MK_CELLO_NEWS A WHERE LANG_CD = #{langCd}
AND (A.TITLE_NM ILIKE CONCAT ('%', #{searchValue}, '%') OR A.NEWS_CCD2 ILIKE CONCAT ('%', #{searchValue}, '%'))
ORDER BY A.NEWS_SEQ_NO DESC LIMIT #{endRow} OFFSET #{startRow}

mapper.insert:
