mapper.select:
SELECT COUNT(*) FROM MK_SQPRD WHERE 1=1 AND LANG_CD = #{langCd} AND SQPRD_CTGRY = #{sqprdCtgry}
AND SQPRD_NM ILIKE CONCAT ('%', #{searchValue}, '%')
AND FN_GETMANAGERNM(INS_PERSON_ID) ILIKE CONCAT ('%', #{searchValue}, '%')

SELECT A.SQPRD_SEQ_NO , A.META_SEQ_NO , A.LANG_CD , A.SQPRD_CTGRY , A.SQPRD_NM , A.SQPRD_ENG_NM , A.SQPRD_ATTN_ID , A.SQPRD_NAVY , TO_CHAR(TO_TIMESTAMP(SVC_VALID_STAT_DATE, 'YYYYMMDD'), 'YYYY-MM-DD') AS SVC_VALID_STAT_DATE , TO_CHAR(TO_TIMESTAMP(SVC_VALID_END_DATE, 'YYYYMMDD'), 'YYYY-MM-DD') AS SVC_VALID_END_DATE , A.SQPRD_SUMMARY_INFO , A.PC_LIST_IMG_PATH , A.PC_LIST_IMG_ORG_FILE_NM , A.PC_LIST_IMG_FILE_NM , A.PC_LIST_IMG_SIZE , A.PC_LIST_IMG_ALT , A.PC_DETL_IMG_PATH , A.PC_DETL_IMG_ORG_FILE_NM , A.PC_DETL_IMG_FILE_NM , A.PC_DETL_IMG_SIZE , A.PC_DETL_IMG_ALT , A.MOBILE_LIST_IMG_PATH , A.MOBILE_LIST_IMG_ORG_FILE_NM , A.MOBILE_LIST_IMG_FILE_NM , A.MOBILE_LIST_IMG_SIZE , A.MOBILE_LIST_IMG_ALT , A.MOBILE_DETL_IMG_PATH , A.MOBILE_DETL_IMG_ORG_FILE_NM , A.MOBILE_DETL_IMG_FILE_NM , A.MOBILE_DETL_IMG_SIZE , A.MOBILE_DETL_IMG_ALT , A.SQPRD_DETL_INFO , A.SQPRD_NOTI_CONTENTS , A.DEP_PORT_CD , A.ARRP_POT_CD , A.SHIP_TO , A.RELTD_SQPRD1 , A.RELTD_SQPRD2 , A.RELTD_SQPRD3 , A.VIEW_TYPE_IMG , A.VIEW_TYPE_IMG_BKGR_COLOR_OPT , A.VIEW_TYPE_IMG_BKGR_COLOR_CCD , A.VIEW_TYPE_IMG_BKGR_COLOR_VAL , A.VIEW_TYPE_IMG_FROM , A.VIEW_TYPE_IMG_CCD , A.VIEW_TYPE_IMG_TO , A.VIEW_TYPE_IMG_HASH_TAG , A.VIEW_TYPE_IMG_PATH , A.VIEW_TYPE_IMG_ORG_FILE_NM , A.VIEW_TYPE_IMG_FILE_NM , A.VIEW_TYPE_IMG_SIZE , A.VIEW_TYPE_IMG_ALT , A.USE_YN , FN_GETCODENM('USE_YN', A.USE_YN, A.LANG_CD) AS USE_YN_NM , A.SRCH_CNT , A.ORDB , TO_CHAR(INS_DTM, 'YYYY-MM-DD') AS INS_DTM , A.INS_PERSON_ID , FN_GETMANAGERNM(A.INS_PERSON_ID) AS INS_PERSON_NM , TO_CHAR(UPD_DTM, 'YYYY-MM-DD') AS UPD_DTM , A.UPD_PERSON_ID , FN_GETMANAGERNM(A.UPD_PERSON_ID) AS UPD_PERSON_NM FROM MK_SQPRD A WHERE 1=1 AND A.LANG_CD = #{langCd} AND A.SQPRD_CTGRY = #{sqprdCtgry}
AND A.SQPRD_NM ILIKE CONCAT ('%', #{searchValue}, '%')
AND FN_GETMANAGERNM(A.INS_PERSON_ID) ILIKE CONCAT ('%', #{searchValue}, '%')
ORDER BY A.SQPRD_SEQ_NO DESC LIMIT #{endRow} OFFSET #{startRow}

mapper.update:
UPDATE MK_SQPRD SET
	ORDB = #{ordb}
WHERE 1=1
AND SQPRD_CTGRY = #{sqprdCtgry}
AND SQPRD_SEQ_NO = #{sqprdSeqNo}::integer

mapper.select:
SELECT COUNT(*) FROM MK_SQPRD WHERE 1=1 AND LANG_CD = #{langCd}
AND SQPRD_SEQ_NO != #{sqprdSeqNo}::integer
AND SQPRD_NM ILIKE CONCAT ('%', #{goodsSearchValue}, '%')

SELECT A.SQPRD_SEQ_NO , A.META_SEQ_NO , A.LANG_CD , A.SQPRD_CTGRY , FN_GETCODENM('GOODS_DIV', A.SQPRD_CTGRY, A.LANG_CD) AS SQPRD_CTGRY_NM , A.SQPRD_NM , A.SQPRD_ENG_NM , A.SQPRD_ATTN_ID , A.SQPRD_NAVY , TO_CHAR(TO_TIMESTAMP(SVC_VALID_STAT_DATE, 'YYYYMMDD'), 'YYYY-MM-DD') AS SVC_VALID_STAT_DATE , TO_CHAR(TO_TIMESTAMP(SVC_VALID_END_DATE, 'YYYYMMDD'), 'YYYY-MM-DD') AS SVC_VALID_END_DATE , A.SQPRD_SUMMARY_INFO , A.PC_LIST_IMG_PATH , A.PC_LIST_IMG_ORG_FILE_NM , A.PC_LIST_IMG_FILE_NM , A.PC_LIST_IMG_SIZE , A.PC_LIST_IMG_ALT , A.PC_DETL_IMG_PATH , A.PC_DETL_IMG_ORG_FILE_NM , A.PC_DETL_IMG_FILE_NM , A.PC_DETL_IMG_SIZE , A.PC_DETL_IMG_ALT , A.MOBILE_LIST_IMG_PATH , A.MOBILE_LIST_IMG_ORG_FILE_NM , A.MOBILE_LIST_IMG_FILE_NM , A.MOBILE_LIST_IMG_SIZE , A.MOBILE_LIST_IMG_ALT , A.MOBILE_DETL_IMG_PATH , A.MOBILE_DETL_IMG_ORG_FILE_NM , A.MOBILE_DETL_IMG_FILE_NM , A.MOBILE_DETL_IMG_SIZE , A.MOBILE_DETL_IMG_ALT , A.SQPRD_DETL_INFO , A.SQPRD_NOTI_CONTENTS , A.DEP_PORT_CD , A.ARRP_POT_CD , A.SHIP_TO , A.RELTD_SQPRD1 , A.RELTD_SQPRD2 , A.RELTD_SQPRD3 , A.VIEW_TYPE_IMG , A.VIEW_TYPE_IMG_BKGR_COLOR_OPT , A.VIEW_TYPE_IMG_BKGR_COLOR_CCD , A.VIEW_TYPE_IMG_BKGR_COLOR_VAL , A.VIEW_TYPE_IMG_FROM , A.VIEW_TYPE_IMG_CCD , A.VIEW_TYPE_IMG_TO , A.VIEW_TYPE_IMG_HASH_TAG , A.VIEW_TYPE_IMG_PATH , A.VIEW_TYPE_IMG_ORG_FILE_NM , A.VIEW_TYPE_IMG_FILE_NM , A.VIEW_TYPE_IMG_SIZE , A.VIEW_TYPE_IMG_ALT , A.USE_YN , FN_GETCODENM('USE_YN', A.USE_YN, A.LANG_CD) AS USE_YN_NM , A.SRCH_CNT , A.ORDB , TO_CHAR(INS_DTM, 'YYYY-MM-DD') AS INS_DTM , A.INS_PERSON_ID , FN_GETMANAGERNM(A.INS_PERSON_ID) AS INS_PERSON_NM , TO_CHAR(UPD_DTM, 'YYYY-MM-DD') AS UPD_DTM , A.UPD_PERSON_ID , FN_GETMANAGERNM(A.UPD_PERSON_ID) AS UPD_PERSON_NM FROM MK_SQPRD A WHERE 1=1 AND LANG_CD = #{langCd}
AND SQPRD_SEQ_NO != #{sqprdSeqNo}::integer
AND A.SQPRD_NM ILIKE CONCAT ('%', #{goodsSearchValue}, '%')
ORDER BY A.SQPRD_SEQ_NO DESC LIMIT #{endRow} OFFSET #{startRow}

