mapper.select:
SELECT A.RPT_SEQ_NO , A.META_SEQ_NO , A.LANG_CD , A.TITLE_NM , A.RPT_CCD , FN_GETCODENM('REPORT_DIV', A.RPT_CCD, A.LANG_CD) AS RPT_CCD_NM , A.SUMMARY_INFO , A.PC_LIST_IMG_ALT , A.DETL_INFO , A.USE_YN , FN_GETCODENM('USE_YN', A.USE_YN, A.LANG_CD) AS USE_YN_NM , A.INS_PERSON_ID , FN_GETMANAGERNM(A.INS_PERSON_ID) AS INS_PERSON_NM , TO_CHAR(A.INS_DTM, 'YYYY-MM-DD') AS INS_DTM , A.UPD_PERSON_ID , FN_GETMANAGERNM(A.UPD_PERSON_ID) AS UPD_PERSON_NM , TO_CHAR(A.UPD_DTM, 'YYYY-MM-DD') AS UPD_DTM , A.SRCH_CNT , A.ORDB FROM MK_RPT A WHERE LANG_CD = #{langCd}
AND A.TITLE_NM ILIKE CONCAT ('%', #{searchValue}, '%')
ORDER BY A.RPT_SEQ_NO DESC LIMIT #{endRow} OFFSET #{startRow}

mapper.insert:
INSERT INTO MK_RPT (
