mapper.select:
SELECT COUNT(*)
FROM MG_VIDEO
WHERE LANG_CD = #{langCd}

	AND VIDEO_CCD = #{searchType}


	AND TITLE_NM ILIKE CONCAT ('%', #{searchValue}, '%')

SELECT A.VIDEO_SEQ_NO , A.META_SEQ_NO , A.LANG_CD , A.VIDEO_CCD , FN_GETCODENM('VIDEO_DIV', A.VIDEO_CCD, A.LANG_CD) AS VIDEO_CCD_NM , A.TITLE_NM , A.TITLE_NM , A.SUMMARY_INFO , A.PC_LIST_IMG_PATH , A.PC_LIST_IMG_ORG_FILE_NM , A.PC_LIST_IMG_FILE_NM , A.PC_LIST_IMG_SIZE , A.PC_LIST_IMG_ALT , A.PC_DETL_IMG_PATH , A.PC_DETL_IMG_ORG_FILE_NM , A.PC_DETL_IMG_FILE_NM , A.PC_DETL_IMG_SIZE , A.PC_DETL_IMG_ALT , A.MOBILE_LIST_IMG_PATH , A.MOBILE_LIST_IMG_ORG_FILE_NM , A.MOBILE_LIST_IMG_FILE_NM , A.MOBILE_LIST_IMG_SIZE , A.MOBILE_LIST_IMG_ALT , A.MOBILE_DETL_IMG_PATH , A.MOBILE_DETL_IMG_ORG_FILE_NM , A.MOBILE_DETL_IMG_FILE_NM , A.MOBILE_DETL_IMG_SIZE , A.MOBILE_DETL_IMG_ALT , FN_GETCODENM('VIDEO_CON_TP', A.CONTENTS_TYPE, A.LANG_CD) AS CONTENTS_TYPE_NM , A.CONTENTS_TYPE , A.CONTENTS , A.DETL_INFO , A.USE_YN , FN_GETCODENM('USE_YN', A.USE_YN, A.LANG_CD) AS USE_YN_NM , A.SRCH_CNT , A.ORDB , TO_CHAR(INS_DTM, 'YYYY-MM-DD') AS INS_DTM , A.INS_PERSON_ID , FN_GETMANAGERNM(A.INS_PERSON_ID) AS INS_PERSON_NM , TO_CHAR(UPD_DTM, 'YYYY-MM-DD') AS UPD_DTM , A.UPD_PERSON_ID , FN_GETMANAGERNM(A.UPD_PERSON_ID) AS UPD_PERSON_NM FROM MG_VIDEO A WHERE LANG_CD = #{langCd}
AND VIDEO_CCD = #{searchType}
AND TITLE_NM ILIKE CONCAT ('%', #{searchValue}, '%')
ORDER BY A.VIDEO_SEQ_NO DESC LIMIT #{endRow} OFFSET #{startRow}

mapper.insert:
INSERT INTO MG_VIDEO (
	  META_SEQ_NO
	, LANG_CD
	, VIDEO_CCD
	, TITLE_NM
	, SUMMARY_INFO
	, PC_LIST_IMG_PATH
	, PC_LIST_IMG_ORG_FILE_NM
	, PC_LIST_IMG_FILE_NM
	, PC_LIST_IMG_SIZE
	, PC_LIST_IMG_ALT
	, PC_DETL_IMG_PATH
	, PC_DETL_IMG_ORG_FILE_NM
	, PC_DETL_IMG_FILE_NM
	, PC_DETL_IMG_SIZE
	, PC_DETL_IMG_ALT
	, MOBILE_LIST_IMG_PATH 
	, MOBILE_LIST_IMG_ORG_FILE_NM
	, MOBILE_LIST_IMG_FILE_NM
	, MOBILE_LIST_IMG_SIZE 
	, MOBILE_LIST_IMG_ALT
	, MOBILE_DETL_IMG_PATH 
	, MOBILE_DETL_IMG_ORG_FILE_NM
	, MOBILE_DETL_IMG_FILE_NM
	, MOBILE_DETL_IMG_SIZE 
	, MOBILE_DETL_IMG_ALT
	, CONTENTS_TYPE
	, CONTENTS
	, DETL_INFO
	, USE_YN
	, INS_DTM
	, INS_PERSON_ID
	, UPD_DTM
	, UPD_PERSON_ID
) VALUES (
	  #{metaSeqNo}::integer
	, #{langCd}
	, #{videoCcd}
	, #{titleNm}
	, #{summaryInfo}
	, #{pcListImgPath}
	, #{pcListImgOrgFileNm}
	, #{pcListImgFileNm}
	, #{pcListImgSize}::integer
	, #{pcListImgAlt}
	, #{pcDetlImgPath}
	, #{pcDetlImgOrgFileNm}
	, #{pcDetlImgFileNm}
	, #{pcDetlImgSize}::integer
	, #{pcDetlImgAlt}
	, #{mobileListImgPath}
	, #{mobileListImgOrgFileNm}
	, #{mobileListImgFileNm}
	, #{mobileListImgSize}::integer
	, #{mobileListImgAlt}
	, #{mobileDetlImgPath}
	, #{mobileDetlImgOrgFileNm}
	, #{mobileDetlImgFileNm}
	, #{mobileDetlImgSize}::integer
	, #{mobileDetlImgAlt}
	, #{contentsType}
	, #{contents}
	, #{detlInfo}
	, #{useYn}
	, NOW()
	, #{insPersonId}
	, NOW()
	, #{updPersonId}
)

mapper.update:
UPDATE MG_VIDEO
SET
	  TITLE_NM				       	 = #{titleNm}
	, VIDEO_CCD				  	     = #{videoCcd}
	, SUMMARY_INFO                   = #{summaryInfo}
	, PC_LIST_IMG_PATH			     = #{pcListImgPath}
	, PC_LIST_IMG_ORG_FILE_NM        = #{pcListImgOrgFileNm}
	, PC_LIST_IMG_FILE_NM            = #{pcListImgFileNm}
	, PC_LIST_IMG_SIZE               = #{pcListImgSize}::integer
	, PC_LIST_IMG_ALT                = #{pcListImgAlt}
	, PC_DETL_IMG_PATH			 	 = #{pcDetlImgPath}
	, PC_DETL_IMG_ORG_FILE_NM      	 = #{pcDetlImgOrgFileNm}
	, PC_DETL_IMG_FILE_NM          	 = #{pcDetlImgFileNm}
	, PC_DETL_IMG_SIZE             	 = #{pcDetlImgSize}::integer
	, PC_DETL_IMG_ALT              	 = #{pcDetlImgAlt}
	, MOBILE_LIST_IMG_PATH			 = #{mobileListImgPath}
	, MOBILE_LIST_IMG_ORG_FILE_NM    = #{mobileListImgOrgFileNm}
	, MOBILE_LIST_IMG_FILE_NM        = #{mobileListImgFileNm}
	, MOBILE_LIST_IMG_SIZE           = #{mobileListImgSize}::integer
	, MOBILE_LIST_IMG_ALT            = #{mobileListImgAlt}
	, MOBILE_DETL_IMG_PATH		 	 = #{mobileDetlImgPath}
	, MOBILE_DETL_IMG_ORG_FILE_NM  	 = #{mobileDetlImgOrgFileNm}
	, MOBILE_DETL_IMG_FILE_NM      	 = #{mobileDetlImgFileNm}
	, MOBILE_DETL_IMG_SIZE         	 = #{mobileDetlImgSize}::integer
	, MOBILE_DETL_IMG_ALT          	 = #{mobileDetlImgAlt}
	, CONTENTS_TYPE					 = #{contentsType}	
	, CONTENTS						 = #{contents}
	, DETL_INFO						 = #{detlInfo}
	, USE_YN               			 = #{useYn}
	, UPD_DTM 			       		 = NOW()
	, UPD_PERSON_ID 			     = #{updPersonId}
WHERE VIDEO_SEQ_NO = #{videoSeqNo}::integer

UPDATE MG_VIDEO SET
	ORDB = #{ordb}
WHERE VIDEO_SEQ_NO = #{videoSeqNo}::integer
